mapper.update:
UPDATE product
prod_name=#{prodName}, prod_detail=#{prodDetail}, manufacture_day=#{manuDate}, price=#{price}, image_file=#{fileName}
WHERE prod_no=#{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT w.product_no, w.customer_id, w.wished_date, p.prod_name, vt.tran_status_code, p.price, p.prod_detail FROM wishlist w, product p, ( SELECT prod_name, NVL(t.tran_status_code,'0') tran_status_code FROM product p, transaction t WHERE p.prod_no=t.prod_no(+) )vt WHERE w.customer_id=#{searchKeyword} AND w.product_no=p.prod_no AND vt.prod_name=p.prod_name
ORDER BY p.price ASC
ORDER BY p.price DESC
)inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.image_file, p.prod_no, p.prod_name, p.price, p.prod_detail, p.reg_date, NVL(t.tran_status_code,'0') tran_status_code, NVL(vt.count,'0') count_liked FROM product p, transaction t, ( SELECT product_no, count(product_no) count FROM wishlist w, product p WHERE w.product_no=p.prod_no GROUP BY product_no )vt WHERE p.prod_no=t.prod_no(+) AND vt.product_no(+)=p.prod_no
AND p.price BETWEEN #{startPrice} AND #{endPrice}
AND p.prod_name like '%${searchKeyword}%'
AND p.price=#{searchKeyword}
ORDER BY p.price ASC
ORDER BY p.price DESC
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*)
FROM(			SELECT product_no 
				FROM wishlist
				WHERE customer_id=#{customerId} AND product_no=#{productNo}
				)countTable

mapper.delete:
 	DELETE 
	FROM wishlist
	WHERE product_no=#{productNo} AND customer_id=#{customerId}

mapper.select:
SELECT COUNT(*)
FROM(			SELECT product_no 
				FROM wishlist
				WHERE
				
						product_no=#{productNo}
				
				
						customer_id=#{customerId}
				
				)countTable

  	SELECT COUNT(*)
  	FROM(			SELECT prod_name , prod_no , price
					FROM product
					
					
							
					 			prod_name like '%${searchKeyword}%'
							
							
					 			price = #{searchKeyword}
							
					
					
					AND price BETWEEN #{startPrice} AND #{endPrice}
					
					 
					) countTable
